//import logo from './logo.svg';
//import './App.css';
import { useState } from 'react';
import React from 'react';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';
import Playlist from './components/Playlist';


function App() {
  const [playlistName, setPlaylistName] = useState('My Playlist');
  const [searchResults, setSearchResults] = useState([
    {id: 1, name:'Track 1', artist:'Artist 1', album:'Album 1', uri: 'spotify:track:1'},
    {id: 2, name:'Track 2', artist:'Artist 2', album:'Album 2', uri: 'spotify:track:2'},
  ]);
  const [playlistTracks, setPlaylistTracks] = useState([]);

  const addTrack = (track) => {
    if (!playlistTracks.some(existingTrack => existingTrack.id === track.id)){
      setPlaylistTracks([...playlistTracks, track]);
    }
  };

  const removeTrack = (track) => {
    setPlaylistTracks(playlistTracks.filter(existingTrack => existingTrack.id != track.id));
  };

  const updatePlaylistName = (newName)=>{
    setPlaylistName(newName);
  };

  const savePlaylist = () =>{
    const trackURIs = playlistTracks.map(track => track.uri);

    // Here, you would typically send the trackURIs to the Spotify API to save the playlist.
    // For now, we'll just log them and reset the state for testing purposes.
    alert("Saving playlist with the following URIs: " + trackURIs.join(', '));

    //Resetting the state
    setPlaylistName('My Playlist');
    setPlaylistTracks([]);
  };

  const searchSpotify = (term) => {
    Spotify.search(term).then(searchResults => {
      setSearchResults(searchResults);
    });
  };

  return (
    <div className="App">
      <SearchBar></SearchBar>
      <div className='App-playlist'>
        <SearchResults searchResults={searchResults} onAdd={addTrack}></SearchResults>
        <Playlist playlistName ={playlistName} tracks ={playlistTracks} onRemove={removeTrack} onNameChange ={updatePlaylistName} onSave = {savePlaylist}></Playlist>
      </div>
    </div>
  );
}

export default App;
